import axios from 'axios';

const API_URL = "http://localhost:5001/api";
export const getAlbums = async () => {
  const response = await axios.get(`${API_URL}/albums`);
  return response.data;
};

export const getAlbumDetails = async (id) => {
  const response = await axios.get(`${API_URL}/${id}`);
  return response.data;
};

export const createAlbum = async (albumData) => {
  const response = await axios.post(API_URL, albumData);
  return response.data;
};

export const updateAlbum = async (id, albumData) => {
  const response = await axios.put(`${API_URL}/${id}`, albumData);
  return response.data;
};

export const updateInventory = async (id, inventoryData) => {
  const response = await axios.put(`${API_URL}/inventory/${id}`, inventoryData);
  return response.data;
};

export const searchAlbums = async (query) => {
  const response = await axios.get(`${API_URL}/search?q=${query}`);
  return response.data;
};
export const getTopSellingAlbums = async () => {
  const response = await axios.get(`${API_URL}/ensembles/top-selling`);
  return response.data;
};
