import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchCompositions } from '../features/compositionSlice';
import LoadingSpinner from './LoadingSpinner';

const CompositionList = () => {
  const dispatch = useDispatch();

  // Убедитесь, что получаете данные из правильного места в store
  const { list, status, error } = useSelector(state => state.compositions);

  useEffect(() => {
    dispatch(fetchCompositions());
  }, [dispatch]);

  // Добавьте логирование для отладки
  console.log('Compositions list:', list);
  console.log('Status:', status);
  console.log('Error:', error);

  if (status === 'loading') return <LoadingSpinner text="Загрузка композиций..." />;
  if (status === 'failed') return <div className="text-red-500">Ошибка: {error}</div>;

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-lg">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800">Музыкальные композиции</h2>
        <Link 
          to="/add-composition" 
          className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors"
        >
          Добавить композицию
        </Link>
      </div>

      {list.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            {/* ... */}
          </table>
        </div>
      ) : (
        <div className="text-center py-8">
          <p className="text-gray-500">Композиции не найдены</p>
          <Link 
            to="/add-composition" 
            className="mt-4 inline-block bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
          >
            Добавить первую композицию
          </Link>
        </div>
      )}
    </div>
  );
};

export default CompositionList;
