import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { addNewAlbum } from '../features/albumSlice';
import MusicianSearch from './MusicianSearch';
import LoadingSpinner from './LoadingSpinner';
import { fetchMusicians } from '../features/musicianSlice';


const AlbumForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // Основные данные формы
  const [formData, setFormData] = useState({
    title: '',
    catalogNumber: '',
    releaseDate: '',
    labelId: '',
    ensembleId: '',
    tracks: []
  });

  // Данные для нового трека
  const [newTrack, setNewTrack] = useState({
    position: '',
    recordingId: ''
  });

  // Выбранный музыкант (сольный исполнитель)
  const [selectedMusician, setSelectedMusician] = useState(null);

  // Состояния UI
  const [errors, setErrors] = useState({});
  const [submitStatus, setSubmitStatus] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Загрузка данных
  const { list: musicians } = useSelector(state => state.musicians);
  const { list: labels } = useSelector(state => state.labels);
  const { status: musiciansStatus } = useSelector(state => state.musicians);
  const { status: labelsStatus } = useSelector(state => state.labels);

  useEffect(() => {
    if (musiciansStatus === 'idle') {
      dispatch(fetchMusicians());
    }
    if (labelsStatus === 'idle') {
      dispatch(fetchLabels());
    }
  }, [dispatch, musiciansStatus, labelsStatus]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleTrackChange = (e) => {
    const { name, value } = e.target;
    setNewTrack(prev => ({ ...prev, [name]: value }));
  };

  const addTrack = () => {
    if (!newTrack.position || !newTrack.recordingId) {
      setErrors({ tracks: 'Заполните все поля трека' });
      return;
    }

    setFormData(prev => ({
      ...prev,
      tracks: [
        ...prev.tracks,
        {
          position: parseInt(newTrack.position),
          recordingId: parseInt(newTrack.recordingId)
        }
      ]
    }));

    setNewTrack({ position: '', recordingId: '' });
    setErrors(prev => ({ ...prev, tracks: '' }));
  };

  const removeTrack = (index) => {
    const newTracks = [...formData.tracks];
    newTracks.splice(index, 1);
    setFormData(prev => ({ ...prev, tracks: newTracks }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Валидация
    const newErrors = {};
    if (!formData.title) newErrors.title = 'Название обязательно';
    if (!formData.catalogNumber) newErrors.catalogNumber = 'Каталожный номер обязателен';
    if (!formData.releaseDate) newErrors.releaseDate = 'Дата выпуска обязательна';
    if (!formData.labelId) newErrors.labelId = 'Выберите лейбл';
    if (formData.tracks.length === 0) newErrors.tracks = 'Добавьте хотя бы один трек';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      setIsSubmitting(true);

      // Формируем данные для отправки
      const payload = {
        ...formData,
        musicianId: selectedMusician?.id || null
      };

      await dispatch(addNewAlbum(payload)).unwrap();

      setSubmitStatus('success');
      setTimeout(() => {
        navigate('/albums');
      }, 1500);
    } catch (error) {
      setSubmitStatus('error');
      setErrors({ server: error.message || 'Ошибка при добавлении альбома' });
    } finally {
      setIsSubmitting(false);
    }
  };

  // Отображение статусов загрузки
  if (musiciansStatus === 'loading' || labelsStatus === 'loading') {
    return <LoadingSpinner text="Загрузка данных..." />;
  }

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-lg">
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-800 mb-2">Добавить новый альбом</h2>
        <p className="text-gray-600">
          Заполните информацию о музыкальном альбоме и его треках
        </p>
      </div>

      {submitStatus === 'success' && (
        <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded-lg">
          <div className="flex items-center">
            <svg className="w-5 h-5 text-green-500 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7"></path>
            </svg>
            <span className="text-green-700 font-medium">
              Альбом успешно добавлен! Вы будете перенаправлены на страницу альбомов.
            </span>
          </div>
        </div>
      )}

      {submitStatus === 'error' && (
        <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
          <div className="flex items-center">
            <svg className="w-5 h-5 text-red-500 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
            </svg>
            <span className="text-red-700 font-medium">
              {errors.server || 'Произошла ошибка при добавлении альбома'}
            </span>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div>
            <label className="block text-gray-700 font-medium mb-2" htmlFor="title">
              Название альбома *
            </label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              className={`w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 transition ${
                errors.title 
                  ? 'border-red-500 focus:ring-red-500' 
                  : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
              }`}
              placeholder="Введите название альбома"
            />
            {errors.title && <p className="mt-1 text-red-500 text-sm">{errors.title}</p>}
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2" htmlFor="catalogNumber">
              Каталожный номер *
            </label>
            <input
              type="text"
              id="catalogNumber"
              name="catalogNumber"
              value={formData.catalogNumber}
              onChange={handleChange}
              className={`w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 transition ${
                errors.catalogNumber 
                  ? 'border-red-500 focus:ring-red-500' 
                  : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
              }`}
              placeholder="Пример: ABC-123"
            />
            {errors.catalogNumber && <p className="mt-1 text-red-500 text-sm">{errors.catalogNumber}</p>}
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2" htmlFor="releaseDate">
              Дата выпуска *
            </label>
            <input
              type="date"
              id="releaseDate"
              name="releaseDate"
              value={formData.releaseDate}
              onChange={handleChange}
              className={`w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 transition ${
                errors.releaseDate 
                  ? 'border-red-500 focus:ring-red-500' 
                  : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
              }`}
            />
            {errors.releaseDate && <p className="mt-1 text-red-500 text-sm">{errors.releaseDate}</p>}
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2" htmlFor="labelId">
              Лейбл *
            </label>
            <select
              id="labelId"
              name="labelId"
              value={formData.labelId}
              onChange={handleChange}
              className={`w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 transition ${
                errors.labelId 
                  ? 'border-red-500 focus:ring-red-500' 
                  : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
              }`}
            >
              <option value="">Выберите лейбл</option>
              {labels.map(label => (
                <option key={label.id} value={label.id}>
                  {label.name}
                </option>
              ))}
            </select>
            {errors.labelId && <p className="mt-1 text-red-500 text-sm">{errors.labelId}</p>}
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2" htmlFor="ensembleId">
              Ансамбль (опционально)
            </label>
            <select
              id="ensembleId"
              name="ensembleId"
              value={formData.ensembleId}
              onChange={handleChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition"
            >
              <option value="">Выберите ансамбль</option>
              {/* В реальном приложении здесь будут ансамбли */}
              <option value="1">Оркестр Москвы</option>
              <option value="2">Квартет им. Чайковского</option>
            </select>
          </div>
        </div>

        {/* Блок с музыкантом */}
        <div className="mb-6 p-4 bg-gray-50 rounded-lg border border-gray-200">
          <h3 className="text-lg font-semibold mb-4 text-gray-800">Сольный исполнитель (опционально)</h3>

          {selectedMusician ? (
            <div className="flex justify-between items-center bg-white p-3 rounded border">
              <div>
                <span className="font-medium">{selectedMusician.name}</span>
                {selectedMusician.roles && (
                  <span className="text-sm text-gray-500 ml-2">
                    ({selectedMusician.roles.join(', ')})
                  </span>
                )}
              </div>
              <button
                type="button"
                onClick={() => setSelectedMusician(null)}
                className="text-red-600 hover:text-red-800 text-sm"
              >
                Удалить
              </button>
            </div>
          ) : (
            <MusicianSearch 
              onSelect={setSelectedMusician} 
              musicians={musicians}
              placeholder="Поиск музыканта по имени..."
            />
          )}
        </div>

        {/* Блок с треками */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-semibold text-gray-800">Треки альбома *</h3>
            {errors.tracks && <p className="text-red-500 text-sm">{errors.tracks}</p>}
          </div>

          {formData.tracks.length > 0 && (
            <div className="mb-6 bg-gray-50 rounded-lg overflow-hidden">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="px-4 py-3 text-left font-medium text-gray-700">#</th>
                    <th className="px-4 py-3 text-left font-medium text-gray-700">ID записи</th>
                    <th className="px-4 py-3 text-right font-medium text-gray-700">Действия</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {formData.tracks.map((track, index) => (
                    <tr key={index} className="hover:bg-gray-100">
                      <td className="px-4 py-3 text-gray-700">{track.position}</td>
                      <td className="px-4 py-3 text-gray-700">{track.recordingId}</td>
                      <td className="px-4 py-3 text-right">
                        <button
                          type="button"
                          onClick={() => removeTrack(index)}
                          className="text-red-500 hover:text-red-700 transition"
                        >
                          Удалить
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          <div className="bg-white border border-gray-300 rounded-lg p-5">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label className="block text-gray-700 mb-2" htmlFor="trackPosition">
                  Позиция трека *
                </label>
                <input
                  type="number"
                  id="trackPosition"
                  name="position"
                  value={newTrack.position}
                  onChange={handleTrackChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="1"
                  min="1"
                />
              </div>

              <div>
                <label className="block text-gray-700 mb-2" htmlFor="trackRecordingId">
                  ID записи *
                </label>
                <input
                  type="number"
                  id="trackRecordingId"
                  name="recordingId"
                  value={newTrack.recordingId}
                  onChange={handleTrackChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="123"
                  min="1"
                />
              </div>

              <div className="flex items-end">
                <button
                  type="button"
                  onClick={addTrack}
                  className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
                >
                  Добавить трек
                </button>
              </div>
            </div>
          </div>
        </div>

        <div className="flex justify-end space-x-4 pt-4 border-t border-gray-200">
          <button
            type="button"
            onClick={() => navigate('/albums')}
            className="px-6 py-3 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 transition-colors font-medium"
          >
            Отмена
          </button>
          <button
            type="submit"
            disabled={isSubmitting}
            className={`px-6 py-3 rounded-lg font-medium transition-colors ${
              isSubmitting
                ? 'bg-blue-400 cursor-not-allowed'
                : 'bg-blue-600 text-white hover:bg-blue-700'
            }`}
          >
            {isSubmitting ? (
              <div className="flex items-center">
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
