import React, { useState } from 'react';

const RecordingSearch = ({ recordings, onSelect, selected }) => {
  const [query, setQuery] = useState('');
  const [filteredRecordings, setFilteredRecordings] = useState([]);

  const handleSearch = (e) => {
    const value = e.target.value;
    setQuery(value);

    if (value.length > 2) {
      const filtered = recordings.filter(recording => 
        recording.composition.title.toLowerCase().includes(value.toLowerCase()) ||
        recording.studio?.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredRecordings(filtered.slice(0, 10));
    } else {
      setFilteredRecordings([]);
    }
  };

  return (
    <div className="relative">
      {selected ? (
        <div className="flex justify-between items-center bg-white p-2 rounded border">
          <div>
            <div className="font-medium">{selected.composition.title}</div>
            <div className="text-sm text-gray-500">
              {selected.studio} • {new Date(selected.recordingDate).toLocaleDateString()}
            </div>
          </div>
          <button
            type="button"
            onClick={() => onSelect(null)}
            className="text-blue-600 hover:text-blue-800 text-sm"
          >
            Изменить
          </button>
        </div>
      ) : (
        <>
          <input
            type="text"
            placeholder="Поиск записи по названию композиции или студии..."
            value={query}
            onChange={handleSearch}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />

          {filteredRecordings.length > 0 && (
            <ul className="absolute z-10 w-full bg-white shadow-lg rounded-b-md border border-gray-200 max-h-60 overflow-y-auto">
              {filteredRecordings.map(recording => (
                <li 
                  key={recording.id}
                  className="p-3 hover:bg-blue-50 cursor-pointer border-b border-gray-100 last:border-b-0"
                  onClick={() => {
                    onSelect(recording);
                    setQuery('');
                    setFilteredRecordings([]);
                  }}
                >
                  <div className="font-medium">{recording.composition.title}</div>
                  <div className="text-sm text-gray-500">
                    {recording.studio} • {new Date(recording.recordingDate).toLocaleDateString()}
                  </div>
                </li>
              ))}
            </ul>
          )}
        </>
      )}
    </div>
  );
};

export default RecordingSearch;
