import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { addNewAlbum } from '../features/albumSlice';
import EnsembleSearch from './EnsembleSearch';
import RecordingSearch from './RecordingSearch';
import api from '../api';
import { toast } from 'react-hot-toast';
import { searchMusicians } from '../services/musicianService';

const AlbumForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    title: '',
    catalogNumber: '',
    releaseDate: '',
  });

  const [tracks, setTracks] = useState([{ position: 1, recording: null }]);
  const [selectedMusician, setSelectedMusician] = useState(null);
  const [selectedEnsemble, setSelectedEnsemble] = useState(null);
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [recordings, setRecordings] = useState([]);
  const [isLoadingRecordings, setIsLoadingRecordings] = useState(true);

  const [musicianSearchResults, setMusicianSearchResults] = useState([]);
  const [isSearchingMusicians, setIsSearchingMusicians] = useState(false);
  const [searchTerm, setSearchTerm] = useState(''); // Добавляем состояние для поискового запроса

  // Загрузка записей (recordings)
  useEffect(() => {
    const fetchRecordings = async () => {
      try {
        setIsLoadingRecordings(true);
        const response = await api.get('/recordings');
        setRecordings(response.data);
      } catch (error) {
        console.error('Failed to fetch recordings', error);
        toast.error('Не удалось загрузить записи');
        setErrors({ server: 'Не удалось загрузить записи' });
      } finally {
        setIsLoadingRecordings(false);
      }
    };

    fetchRecordings();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleTrackChange = (index, field, value) => {
    const newTracks = [...tracks];
    newTracks[index][field] = value;
    setTracks(newTracks);
  };

  const addTrack = () => {
    setTracks([...tracks, { position: tracks.length + 1, recording: null }]);
  };

  const removeTrack = (index) => {
    if (tracks.length > 1) {
      const newTracks = tracks.filter((_, i) => i !== index)
        .map((track, i) => ({ ...track, position: i + 1 }));
      setTracks(newTracks);
    }
  };

  // Поиск музыкантов с защитой от коротких запросов
  const handleMusicianSearch = async (searchTerm) => {
    setSearchTerm(searchTerm); // Сохраняем текущий поисковый запрос

    // Не выполняем поиск при коротких запросах
    if (searchTerm.trim().length < 2) {
      setMusicianSearchResults([]);
      return;
    }

    setIsSearchingMusicians(true);
    try {
      const response = await searchMusicians(searchTerm);
      setMusicianSearchResults(response);
    } catch (error) {
      // Игнорируем ошибку "Query must be at least 2 characters"
      if (!error.message.includes('Query must be at least 2 characters')) {
        console.error('Musician search error:', error);
        toast.error('Ошибка при поиске музыкантов');
      }
    } finally {
      setIsSearchingMusicians(false);
    }
  };

  // Выбор музыканта
  const handleMusicianSelect = (musician) => {
    setSelectedMusician(musician);
    setSelectedEnsemble(null);
    setMusicianSearchResults([]);
    setSearchTerm(''); // Очищаем поле поиска после выбора
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) newErrors.title = 'Название обязательно';
    if (!formData.catalogNumber.trim()) newErrors.catalogNumber = 'Каталожный номер обязателен';
    if (!formData.releaseDate) newErrors.releaseDate = 'Дата выпуска обязательна';

    if (!selectedMusician && !selectedEnsemble) {
      newErrors.performer = 'Выберите сольного исполнителя или ансамбль';
    }

    const invalidTracks = tracks.some(track => !track.recording);
    if (invalidTracks) newErrors.tracks = 'Для всех треков нужно выбрать запись';

    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formErrors = validateForm();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }

    try {
      setIsSubmitting(true);

      const payload = {
        title: formData.title,
        catalogNumber: formData.catalogNumber,
        releaseDate: formData.releaseDate,
        tracks: tracks.map(track => ({
          position: track.position,
          recordingId: track.recording.id
        }))
      };

      if (selectedMusician) {
        payload.musicianId = selectedMusician.id;
      } else if (selectedEnsemble) {
        payload.ensembleId = selectedEnsemble.id;
      }

      await dispatch(addNewAlbum(payload)).unwrap();

      toast.success('Альбом успешно добавлен!');
      setTimeout(() => {
        navigate('/albums');
      }, 1500);
    } catch (error) {
      console.error('Album creation error:', error);
      toast.error(`Ошибка при добавлении альбома: ${error.message || 'Неизвестная ошибка'}`);
      setErrors({ server: error.message || 'Ошибка при добавлении альбома' });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Добавить новый альбом</h2>

      {errors.server && (
        <div className="mb-4 p-3 bg-red-100 text-red-700 rounded">
          {errors.server}
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-gray-700 font-medium mb-2" htmlFor="title">
              Название альбома *
            </label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              className={`w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 transition ${
                errors.title ? 'border-red-500 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'
              }`}
              placeholder="Введите название альбома"
            />
            {errors.title && <p className="mt-1 text-red-500 text-sm">{errors.title}</p>}
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2" htmlFor="catalogNumber">
              Каталожный номер *
            </label>
            <input
              type="text"
              id="catalogNumber"
              name="catalogNumber"
              value={formData.catalogNumber}
              onChange={handleChange}
              className={`w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 transition ${
                errors.catalogNumber ? 'border-red-500 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'
              }`}
              placeholder="Пример: ABC-123"
            />
            {errors.catalogNumber && <p className="mt-1 text-red-500 text-sm">{errors.catalogNumber}</p>}
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2" htmlFor="releaseDate">
              Дата выпуска *
            </label>
            <input
              type="date"
              id="releaseDate"
              name="releaseDate"
              value={formData.releaseDate}
              onChange={handleChange}
              className={`w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 transition ${
                errors.releaseDate ? 'border-red-500 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'
              }`}
            />
            {errors.releaseDate && <p className="mt-1 text-red-500 text-sm">{errors.releaseDate}</p>}
          </div>
        </div>

        {/* Исполнитель */}
        <div className="space-y-4">
          <h3 className="text-lg font-semibold text-gray-800">Исполнитель *</h3>

          {errors.performer && (
            <p className="text-red-500 text-sm">{errors.performer}</p>
          )}

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* Сольный исполнитель */}
            <div>
              <label className="block text-gray-700 mb-2">Сольный исполнитель</label>
              <div className="relative">
                <input
                  type="text"
                  placeholder="Поиск музыканта..."
                  value={searchTerm} // Контролируемое значение
                  className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  onChange={(e) => {
                    setSearchTerm(e.target.value);
                    handleMusicianSearch(e.target.value);
                  }}
                />

                {isSearchingMusicians && (
                  <div className="absolute inset-y-0 right-0 flex items-center pr-3">
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
                  </div>
                )}

                {musicianSearchResults.length > 0 && (
                  <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-y-auto">
                    {musicianSearchResults.map(musician => (
                      <div
                        key={musician.id}
                        className="p-3 hover:bg-blue-50 cursor-pointer border-b border-gray-100 last:border-b-0"
                        onClick={() => handleMusicianSelect(musician)}
                      >
                        <div className="font-medium">{musician.name}</div>
                        <div className="text-sm text-gray-600">
                          {musician.country} • {musician.roles?.join(', ')}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Отображение выбранного музыканта */}
              {selectedMusician && (
                <div className="mt-2 p-3 bg-green-50 border border-green-200 rounded-md flex justify-between items-center">
                  <div>
                    <span className="font-medium">Выбрано:</span> {selectedMusician.name}
                  </div>
                  <button
                    onClick={() => {
                      setSelectedMusician(null);
                      setSearchTerm(''); // Очищаем поле поиска
                    }}
                    className="text-red-600 hover:text-red-800 font-bold"
                  >
                    ×
                  </button>
                </div>
              )}
            </div>

            {/* Ансамбль */}
            <div>
              <label className="block text-gray-700 mb-2">Ансамбль</label>
              <EnsembleSearch 
                onSelect={(ensemble) => {
                  setSelectedEnsemble(ensemble);
                  setSelectedMusician(null);
                  setSearchTerm(''); // Очищаем поле поиска музыканта
                }}
                selected={selectedEnsemble}
                onClear={() => setSelectedEnsemble(null)}
              />
            </div>
          </div>
        </div>

        {/* Треки */}
        <div className="space-y-4">
          <div className="flex justify-between items-center">
            <h3 className="text-lg font-semibold text-gray-800">Треки альбома *</h3>
            {errors.tracks && <p className="text-red-500 text-sm">{errors.tracks}</p>}
          </div>

          {isLoadingRecordings ? (
            <div className="text-center py-4">
              <p className="text-gray-600">Загрузка записей...</p>
            </div>
          ) : (
            <>
              {tracks.map((track, index) => (
                <div key={index} className="bg-gray-50 p-4 rounded-lg border border-gray-200 relative">
                  <button
                    type="button"
                    onClick={() => removeTrack(index)}
                    className="absolute top-2 right-2 text-red-500 hover:text-red-700 text-xl"
                    disabled={tracks.length <= 1}
                    aria-label="Удалить трек"
                  >
                    &times;
                  </button>

                  <div className="grid grid-cols-12 gap-4 items-center">
                    <div className="col-span-2">
                      <label className="block text-gray-700 mb-1">Позиция</label>
                      <input
                        type="number"
                        value={track.position}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md"
                        min="1"
                        disabled
                      />
                    </div>

                    <div className="col-span-10">
                      <label className="block text-gray-700 mb-1">Запись *</label>
                      <RecordingSearch 
                        recordings={recordings}
                        onSelect={(recording) => handleTrackChange(index, 'recording', recording)}
                        selected={track.recording}
                      />
                    </div>
                  </div>
                </div>
              ))}

              <button
                type="button"
                onClick={addTrack}
                className="flex items-center px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 transition-colors"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" clipRule="evenodd" />
                </svg>
                Добавить трек
              </button>
            </>
          )}
        </div>

        {/* Кнопки отправки */}
        <div className="flex justify-end space-x-4 pt-4 border-t border-gray-200">
          <button
            type="button"
            onClick={() => navigate('/albums')}
            className="px-6 py-3 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 transition-colors font-medium"
          >
            Отмена
          </button>
          <button
            type="submit"
            disabled={isSubmitting || isLoadingRecordings}
            className={`px-6 py-3 rounded-lg font-medium transition-colors ${
              isSubmitting || isLoadingRecordings
                ? 'bg-blue-400 text-white cursor-not-allowed'
                : 'bg-blue-600 text-white hover:bg-blue-700'
            }`}
          >
            {isSubmitting ? 'Сохранение...' : 'Сохранить альбом'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AlbumForm;
