import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchTopSellingAlbums } from '../features/albumSlice';

const SalesDashboard = () => {
  const dispatch = useDispatch();
  const { topSelling, status, error } = useSelector(state => state.ensembles);

  useEffect(() => {
    dispatch(fetchTopSellingAlbums());
  }, [dispatch]);

  if (status === 'loading') return <div className="text-center py-4">Загрузка данных...</div>;
  if (error) return <div className="text-red-500 text-center py-4">Ошибка: {error}</div>;

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Лидеры продаж текущего года</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {topSelling.map(item => (
          <div key={item.id} className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow">
            <div className="p-5">
              <h3 className="text-xl font-semibold text-gray-900 mb-2">
                {item.albumTitle}
              </h3>

              <p className="text-gray-600 mb-1">
                <span className="font-medium">Каталожный номер:</span> {item.catalogNumber}
              </p>

              {item.ensembleName && (
                <p className="text-gray-600 mb-1">
                  <span className="font-medium">Ансамбль:</span> {item.ensembleName}
                </p>
              )}

              <p className="text-gray-600 mb-1">
                <span className="font-medium">Продано в этом году:</span> {item.currentYearSales}
              </p>

              <p className="text-gray-600 mb-3">
                <span className="font-medium">Розничная цена:</span> ${item.retailPrice.toFixed(2)}
              </p>

              <div className="mt-4 bg-gray-100 p-3 rounded">
                <p className="text-gray-700">
                  <span className="font-medium">Осталось на складе:</span> {item.unsold}
                </p>
                <p className="text-gray-700">
                  <span className="font-medium">Продано в прошлом году:</span> {item.lastYearSales}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {topSelling.length === 0 && (
        <div className="text-center py-8 text-gray-500">
          Нет данных о продажах за текущий год
        </div>
      )}
    </div>
  );
};

export default SalesDashboard;
