import React, { useState, useEffect } from 'react';
import { searchMusicians } from '../services/musicianService';
import api from '../api';


const MusicianSearch = ({ onSelect, selected, onClear }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);

  // При изменении выбранного музыканта обновляем поле ввода
  useEffect(() => {
    if (selected) {
      setQuery(selected.name);
    }
  }, [selected]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (query.length < 2) return;

    setIsSearching(true);
    try {
      const data = await searchMusicians(query);
      setResults(data);
    } catch (error) {
      console.error('Search error:', error);
    } finally {
      setIsSearching(false);
    }
  };

  const handleSelect = (musician) => {
    onSelect(musician); // Передаем полный объект музыканта
    setResults([]); // Очищаем результаты поиска
  };

  return (
    <div className="relative">
      <form onSubmit={handleSearch} className="flex">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Поиск музыканта..."
          className="flex-grow px-4 py-2 border border-gray-300 rounded-l-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded-r-md hover:bg-blue-700"
        >
          Найти
        </button>
      </form>

      {selected && (
        <div className="mt-2 p-3 bg-green-50 border border-green-200 rounded-md flex justify-between items-center">
          <div>
            <span className="font-medium">Выбрано:</span> {selected.name}
          </div>
          <button
            onClick={onClear}
            className="text-red-600 hover:text-red-800 font-bold"
          >
            &times;
          </button>
        </div>
      )}

      {isSearching && (
        <div className="mt-2 p-2 bg-gray-100 text-center rounded-md">
          Поиск...
        </div>
      )}

      {results.length > 0 && !selected && (
        <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-y-auto">
          {results.map(musician => (
            <div
              key={musician.id}
              className="p-3 hover:bg-blue-50 cursor-pointer border-b border-gray-100 last:border-b-0"
              onClick={() => handleSelect(musician)}
            >
              <div className="font-medium">{musician.name}</div>
              <div className="text-sm text-gray-600">
                {musician.country} • {musician.roles?.join(', ')}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MusicianSearch;
