import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios'; // Добавьте этот импорт
import {
  getAlbums,
  getAlbumDetails,
  createAlbum,
  updateAlbum,
  updateInventory,
  searchAlbums as apiSearchAlbums, // Переименовали для избежания конфликта
  getTopSellingAlbums as apiGetTopSellingAlbums // Переименовали
} from '../services/albumService';


export const fetchAlbums = createAsyncThunk(
  'albums/fetchAll',
  async () => {
    const response = await api.get('/albums');
    return response.data;
  }
);


export const fetchAlbumDetails = createAsyncThunk(
  'albums/fetchDetails',
  async (id) => {
    return await getAlbumDetails(id);
  }
);

export const addNewAlbum = createAsyncThunk(
  'albums/addNew',
  async (albumData) => {
    return await createAlbum(albumData);
  }
);

export const modifyAlbum = createAsyncThunk(
  'albums/update',
  async ({ id, data }) => {
    return await updateAlbum(id, data);
  }
);

export const modifyInventory = createAsyncThunk(
  'albums/updateInventory',
  async ({ id, data }) => {
    return await updateInventory(id, data);
  }
);

export const searchAlbumCatalog = createAsyncThunk(
  'albums/search',
  async (query) => {
    const response = await axios.get(`/api/albums/search?q=${query}`);
    return response.data;
  }
);
export const fetchTopSellingAlbums = createAsyncThunk(
  'albums/fetchTopSelling',
  async () => {
    const response = await api.get('/top-selling');
    return response.data;
  }
);


const albumSlice = createSlice({
  name: 'albums',
  initialState: {
    list: [],
    currentAlbum: null,
    searchResults: [],
    topSelling: [],
    status: 'idle',
    error: null,
    pagination: {}
  },
  reducers: {
    clearCurrentAlbum: (state) => {
      state.currentAlbum = null;
    },
    clearSearchResults: (state) => {
      state.searchResults = [];
    }
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchAlbums.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchAlbums.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.list = action.payload.data || [];
        state.pagination = action.payload.pagination || {};
      })
      .addCase(fetchAlbums.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(fetchAlbumDetails.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchAlbumDetails.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.currentAlbum = action.payload;
      })
      .addCase(fetchAlbumDetails.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(addNewAlbum.fulfilled, (state, action) => {
        state.list = [...state.list, action.payload];
      })
      .addCase(modifyAlbum.fulfilled, (state, action) => {
        state.status = 'succeeded';
        const index = state.list.findIndex(a => a.id === action.payload.id);
        if (index !== -1) {
          state.list[index] = action.payload;
        }
        if (state.currentAlbum?.id === action.payload.id) {
          state.currentAlbum = action.payload;
        }
      })
      .addCase(modifyInventory.fulfilled, (state, action) => {
        state.status = 'succeeded';
        if (state.currentAlbum) {
          state.currentAlbum.inventories = [action.payload];
        }
        const albumIndex = state.list.findIndex(a => 
          a.inventories?.some(i => i.id === action.payload.id)
        );
        if (albumIndex !== -1) {
          state.list[albumIndex].inventories = [action.payload];
        }
      })
      .addCase(searchAlbumCatalog.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(searchAlbumCatalog.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.searchResults = action.payload;
      })
      .addCase(searchAlbumCatalog.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(fetchTopSellingAlbums.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchTopSellingAlbums.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.topSelling = action.payload.map(item => ({
          id: item.id,
          albumTitle: item.title,
          catalogNumber: item.catalogNumber,
          ensembleName: item.ensemble,
          currentYearSales: item.sales,
          retailPrice: item.retailPrice,
          unsold: item.unsold
        }));
      })
      .addCase(fetchTopSellingAlbums.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      });
  }
});

export const { clearCurrentAlbum, clearSearchResults } = albumSlice.actions;
export default albumSlice.reducer;
